sys_ux_client_script.script:
async function script({api, event, imports, helpers}) { 
    console.log("Visual Board Loader Script");
    const {
        'sn_vtb.notificationHandler': notificationHandlerInclude,
        'sn_vtb.constants': constantsInclude,
        'sn_vtb.stateUpdates': stateUpdatesInclude
    } = imports;
    const {
        showLoader
    } = notificationHandlerInclude();
    const {
        MENU_ITEMS
    } = constantsInclude();
    const {
        setTranslatedMessages
    } = stateUpdatesInclude();

  showLoader(api);

  var tranlatedMessages = {};
  for (const [key, value] of Object.entries(MENU_ITEMS)) {
        var translated = await helpers.translate(value);
        tranlatedMessages[value] = translated;
  }
  setTranslatedMessages(api, tranlatedMessages);

  //api.data.visualBoardLoadCompositeDataBroker.refresh()
 }
